import config from "../config";


class WebsocketController {
    constructor() {
        this.current = null;
    }

    connect = () => {
        this.current = new WebSocket(`${config.ws_url}/send/user/`);
    }

    disconnect = () => {
        if (this.current !== null) {
            this.current.close()
            this.current = null;
        }
    }

    send = (message) => {
        if (this.current === null)
            return;

        this.current.send(message);
    }
}

let connection = new WebsocketController();

export default connection;
