import Cookies from "js-cookie";

function auth_headers() {
    return (Cookies.get('access_token') !== null) ? {
        headers: `Authorization: ${Cookies.get('token_type')} ${Cookies.get('access_token')}`
    } : {};
}

export default auth_headers;
