import '../css/form.css';
import axios from "axios";
import {useCookies} from "react-cookie";
import {currentUser} from "../hooks/current_user";
import {Navigate} from "react-router-dom";
import setCurrentUser from "../hooks/auth_user";

function Registration() {
    const cookies = useCookies(["token"]);

    async function Submit(data) {
        data.preventDefault();

        if (data.target[2].value.toLowerCase() !== data.target[3].value.toLowerCase()) {
            alert('Пароли не совпадают! Пошел нахуй!');
            return;
        }

        let response_data = {
            'username': data.target[0].value,
            'email': data.target[1].value,
            'password': data.target[2].value,
        }

        const response = await axios.post('http://localhost:443/auth/register', response_data);

        await setCurrentUser(response.data['email'], data.target[2].value, cookies);

        window.location.href = '/';
    }

    return (currentUser() === null) ? (
        <form className='form-registration' onSubmit={Submit}>
            <h3>username</h3>
            <input type='login'/>
            <h3>email</h3>
            <input type='email'/>
            <h3>password</h3>
            <input type='password'/>
            <h3>repeat password</h3>
            <input type='password'/>
            <button>Submit</button>
        </form>
    ) : (
        <Navigate to='/'/>
    );
}

export default Registration;
