import {useCookies} from "react-cookie";

function useRequestConfig() {
    const cookies = useCookies(["token"]);

    return (cookies[0].access_token !== null) ? {
        headers: `Authorization: ${cookies[0].token_type} ${cookies[0].access_token}`
    } : {};
}

export default useRequestConfig;
