import React, {useCallback, useEffect, useRef, useState} from 'react';
import {Navigate} from "react-router-dom";
import axios from "axios";

import {auth_headers, clearCurrentUser, currentUser, currentUserExist} from "../../controllers/userController";
import styles from '../../css/index.module.css';
import config from "../../config";
import Message from "./MainMessage";
import connection from "../../controllers/websocketController";
import {faFrog, faPaperPlane} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import LeftPanel from "./MainLeftPanel";
import SearchBar from "./MainSearchBar";

function Main() {
    const user = currentUser();

    const messageContent = useRef(null);
    const last_message = useRef(null);
    const [chat_list, setChats] = useState(null);
    const [filter, setFilter] = useState(null);
    const [chat, setChat] = useState({});

    console.log('render');

    const addMessage = useCallback((message) => {
        setChat({...chat, messages: [message, ...chat.messages]});
    }, [chat]);

    useEffect(() => {
        axios.get(`${config.url}/chat/all`, auth_headers()).then((response) => {
            const dict = {};

            Object.entries(response.data.items).map(([_, value]) => value).forEach(({id, ...rest}) => dict[id] = rest);

            setChats(dict);
        }).catch((error) => {
            if (error.response.request.status === 401) {
                clearCurrentUser();
                window.location.href = '/auth'
            }
        });

        connection.connect();
    }, []);

    useEffect(() => {
        if (chat?.id === undefined || last_message.current === null) return;

        if (chat.messages[0].author.id === currentUser().id) {
            console.log('after');
            last_message.current.scrollIntoView({ behavior: "smooth" });
        }
    }, [chat?.id, chat.messages])

    useEffect(() => {
        connection.onmessage((event) => {
            const message_data = JSON.parse(event.data);

            let {chat_id, ...content} = message_data;

            if (chat_id === chat.id) addMessage(content);

            if (chat_id in chat_list) {
                chat_list[chat_id].last_message = content;
                setChats(chat_list);
            }
        });
    }, [addMessage, chat, chat_list]);

    async function getChatMessages(chat_data) {
        if (chat.id === chat_data.id) return;

        axios.get(`${config.url}/message/chat/${chat_data.id}`, auth_headers()).then((response) => {
            setChat({id: Number(chat_data.id), interlocutor: chat_data.interlocutor,
                messages: Object.entries(response.data.items).map(([_, value]) => value)});
        })
    }

    function sendMessage() {
        let message = messageContent.current.innerHTML.replaceAll('&nbsp;', '');

        message = message.replaceAll('<br>', '\n').trim();

        messageContent.current.innerHTML = '';

        if (message === '') return;

        axios.post(`${config.url}/message/chat/${chat.id}`, {},
            {...auth_headers(), params: { message: message }}).then(
            (response) => {
                addMessage({author: currentUser(), ...response.data});

                chat_list[chat.id].last_message = response.data;
                setChats(chat_list);
            }
        );
    }

    function filter_chats(filter_string) {
        if (filter_string === '') { setFilter(null); return; }

        setFilter(filter_string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'));
    }

    return (currentUserExist()) ? (
        <div className={styles.base}>
            <nav>
                <header>
                    <button onClick={() => { clearCurrentUser() } }><h3>Welcome, {user.username}</h3></button>
                </header>
                <SearchBar filter_function={filter_chats}/>
                <LeftPanel filter={filter} chats={chat_list} onClickFunction={getChatMessages}/>
            </nav>
            <main>
            {  chat?.id === undefined ? <FontAwesomeIcon className={styles.frogIcon} icon={faFrog} /> : (
                <>
                    <div className={styles.messageContainer}>
                        { chat.messages.map((data) => <Message key={data.id} message={data} style={
                            currentUser().id === data.author.id ? styles.messageOwn : styles.messageInterlocutor
                        } ref={data.id === chat.messages[0].id ? last_message : null}/>) }
                    </div>
                    <footer>
                        <div className={styles.messageInput} contentEditable={true} ref={messageContent}/>

                        <FontAwesomeIcon onClick={sendMessage} icon={faPaperPlane} style={{color: "black"}} />
                    </footer>
                </>
            ) }
            </main>
        </div>
    ) : (<Navigate to="/auth"/>);
}


export default Main;
