import formatTime from "./formatTime";

function Message({ message, style }) {
    return (
        <div className={style}>
            <p>{ String(message.content).replaceAll('<br>', '\n') }</p>
            <p className={style.messageTime}> { formatTime(message.created_at) } </p>
        </div>
    )
}

export default Message;
