import '../css/form.css';
import axios from "axios";
import Login from "../hooks/Login";
import {useCookies} from "react-cookie";

function Registration() {
    const [cookies, setCookie, useCookie] = useCookies(["token"]);

    async function Submit(data) {
        data.preventDefault();

        if (data.target[2].value.toLowerCase() !== data.target[3].value.toLowerCase()) {
            alert('Пароли не совпадают! Пошел нахуй!');
            return;
        }

        let response_data = {
            'username': data.target[0].value,
            'email': data.target[1].value,
            'password': data.target[2].value,
        }

        axios.post('http://localhost:443/auth/register', response_data).then((response) => {
                console.log(response);
                Login(data.target[0].value, data.target[1].value).then((data) => {
                    for (const [key, value] of Object.entries(data)) {
                        setCookie(key, value);
                    }
                }).catch((error) => {
                    console.log(error);
                });
            }).catch((error) => {
                alert(`Ошибка. ${error.response.data['detail']}`);
                console.log(error);
            })
    }

    return (
        <form className='form-registration' onSubmit={Submit}>
            <h3>username</h3>
            <input type='login'/>
            <h3>email</h3>
            <input type='email'/>
            <h3>password</h3>
            <input type='password'/>
            <h3>repeat password</h3>
            <input type='password'/>
            <button>Submit</button>
        </form>
    );
}

export default Registration;
