import styles from "../../css/index.module.css";
import React from "react";

function Chat({ data, onClickFunction }) {
    function formatTime(data) {
        const date = new Date(Date.parse(data));

        let hour = new Intl.DateTimeFormat('ru', {hour: 'numeric'}).format(date);
        let minute = new Intl.DateTimeFormat('en', {minute: '2-digit'}).format(date);

        return `${hour}:${minute}`;
    }

    return (
        <div className={styles.chat} onClick={() => onClickFunction(data.id, data.interlocutor.id)}>
            <div>
                <h4>{data["interlocutor"].username}</h4>
                <p className={styles.time}>{formatTime(data["last_message"]["created_at"])}</p>
            </div>
            <p>{data["last_message"].content}</p>
        </div>
    )
}

export default Chat;
