import React, {useEffect, useState} from 'react';
import {Navigate} from "react-router-dom";

import {auth_headers, clearCurrentUser, currentUser} from "../controllers/UserController";

import styles from '../css/index.module.css';
import axios from "axios";

function Chat(username, message) {
    return (
        <div className={styles.chat}>
            <h4>{username}</h4>
            <p>{message}</p>
        </div>

    )
}

function Main() {
    const [chats, setChats] = useState([]);

    useEffect(() => {
        async function getChats() {
            const config = auth_headers();

            config['body'] = {
                limit: 50,
                offset: 0,
            }

            const response = await axios.get(
                'http://localhost:443/chat/all',
                config
            );

            const data = await response.json();
            setChats(data.items)
        }

        if (!chats) {
            getChats();
        }
    }, []);

    const user = currentUser();

    return (user !== null) ? (
        <>
            <div className={styles.header}>
                <div className={styles.welcome}>
                    <h1>Welcome, {user.username}!</h1>
                    <button type='button' onClick={() => {clearCurrentUser(); window.location.href = '/auth';}}>LogOut</button>
                </div>
                <div className={styles.find}>
                    <input/>
                    <button type="button">Find</button>
                </div>
            </div>
            <div className={styles.chatList}>
                {chats.map((data) => <Chat username={1} message={'2'}/>)};
            </div>
        </>
    ) : (<Navigate to="/auth"/>);
}

export default Main;
