import '../css/form.css';
import GetToken from "../hooks/token";
import {useCookies} from "react-cookie";
import axios from "axios";
import {currentUser, setUser} from "../hooks/current_user";


function Authorization() {
    const cookies = useCookies(["token"]);

    async function Submit(data) {
        data.preventDefault();

        GetToken(data.target[0].value, data.target[1].value).then((data) => {
            for (const [key, value] of Object.entries(data)) {
                cookies[1](key, value, { path: "/" });
            }

            axios.get('http://localhost:443/users/me', {
                headers: `Authorization: ${cookies[0].token_type} ${cookies[0].access_token}`
            }).then((response) => {
                setUser(response.data);
            }).catch((error) => {
                console.log(error);
            })
        }).catch((error) => {
            console.log(error);
        });
    }

    return (
        <form className='form-registration' onSubmit={Submit}>
            <h3>email</h3>
            <input type='email'/>
            <h3>password</h3>
            <input type='password'/>
            <button>Submit</button>
        </form>
    );
}

export default Authorization;
