import '../css/form.css';
import {currentUser} from "../hooks/current_user";
import {Navigate} from "react-router-dom";
import setCurrentUser from "../hooks/auth_user";

function Authorization() {
    async function Submit(data) {
        data.preventDefault();

        await setCurrentUser(data.target[0].value, data.target[1].value);

        window.location.href = '/';
    }

    return (currentUser() === null) ? (
        <form className='form-registration' onSubmit={Submit}>
            <h3>email</h3>
            <input type='email'/>
            <h3>password</h3>
            <input type='password'/>
            <div>
                <button>Submit</button>
                <button onClick={() => window.location.href = '/reg'}>Have no account</button>
            </div>
        </form>
    ) : (
        <Navigate to="/"/>
    );
}

export default Authorization;
