import React, {useEffect, useState} from 'react';
import {Navigate} from "react-router-dom";
import axios from "axios";

import {auth_headers, clearCurrentUser, currentUser, currentUserExist} from "../../controllers/userController";
import styles from '../../css/index.module.css';
import SelectChat from "./MainSelectChat";


function Main() {
    const [chats, setChats] = useState([]);

    useEffect(() => {
        async function getChats() {
            try {
                const response = await axios.get('http://localhost:443/chat/all', auth_headers());

                setChats(response.data.items);
            }
            catch (error) {
                console.log(error);
            }
        }

        if (chats.length === 0) {
            getChats();
        }
    }, [chats.length]);

    const user = currentUser();

    return (currentUserExist(user)) ? (
        <>
            <header>
                <h1>Welcome, {user.username}!</h1>
                <button type='button' onClick={() => {clearCurrentUser(); window.location.href = '/auth';}}>LogOut</button>
            </header>
            <div id={styles.main}>
                <div id={styles.chatList}>
                    {chats.map((data, index) => <SelectChat key={index} data={data}/>)}
                </div>
                <div id={styles.messageField}>

                </div>
            </div>
        </>
    ) : (<Navigate to="/auth"/>);
}

export default Main;
