import '../css/form.css';
import axios from "axios";
import GetToken from "../hooks/token";
import {useCookies} from "react-cookie";
import {currentUser, setUser} from "../hooks/current_user";
import {Navigate} from "react-router-dom";
import requestConfig from "../hooks/request_config";

function Registration() {
    const cookies = useCookies(["token"]);

    async function Submit(data) {
        data.preventDefault();

        if (data.target[2].value.toLowerCase() !== data.target[3].value.toLowerCase()) {
            alert('Пароли не совпадают! Пошел нахуй!');
            return;
        }

        let response_data = {
            'username': data.target[0].value,
            'email': data.target[1].value,
            'password': data.target[2].value,
        }

        axios.post('http://localhost:443/auth/register', response_data).then((response) => {
            GetToken(response.data['email'], data.target[2].value).then((data) => {
                for (const [key, value] of Object.entries(data)) {
                    cookies[1](key, value, { path: "/" });
                }

                axios.get('http://localhost:443/users/me', requestConfig(cookies[0])).then((response) => {
                    setUser(response.data);
                }).catch((error) => {
                    console.log(error);
                })
            }).catch((error) => {
                console.log(error);
            });

            axios.get('http://localhost:443/users/me').then((response) => {
                console.log(response);
            })
        }).catch((error) => {
            alert(`Ошибка. ${error.response.data['detail']}`);
            console.log(error);
        })
    }

    return (currentUser() === null) ? (
        <form className='form-registration' onSubmit={Submit}>
            <h3>username</h3>
            <input type='login'/>
            <h3>email</h3>
            <input type='email'/>
            <h3>password</h3>
            <input type='password'/>
            <h3>repeat password</h3>
            <input type='password'/>
            <button>Submit</button>
        </form>
    ) : (
        <Navigate to='/'/>
    );
}

export default Registration;
