import React, {useCallback, useEffect, useRef, useState} from 'react';
import {Navigate} from "react-router-dom";
import axios from "axios";
import { v4 as keygen } from 'uuid';

import {auth_headers, clearCurrentUser, currentUser, currentUserExist} from "../../controllers/userController";
import styles from '../../css/index.module.css';
import Chat from "./MainSelectChat";
import config from "../../config";
import Message from "./MainMessage";
import connection from "../../controllers/websocketController";
import {faMagnifyingGlass, faPaperPlane} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import $ from 'jquery';

function Main() {
    const user = currentUser();

    const messageContent = useRef(null);
    const [chat_list, setChats] = useState(null);
    const [filtered_list, setFilter] = useState(null);
    const [chat, setChat] = useState({});

    const addMessage = useCallback((message, style) => {
        setChat({...chat, messages: [<Message key={keygen()} message={message} style={style}/>, ...chat.messages]});
    }, [chat]);

    useEffect(() => {
        axios.get(`${config.url}/chat/all`, auth_headers()).then((response) => {
            let result = {};

            for (let index = 0; index < response.data.total; index++) {
                let item = response.data.items[index];

                result[item.id] = item;
            }

            setChats(result);
        }).catch((error) => {
            if (error.response.request.status === 401) {
                clearCurrentUser();
                window.location.href = '/auth'
            }
        });

        connection.connect();
    }, []);

    useEffect(() => {
        connection.onmessage((event) => {
            const message_data = JSON.parse(event.data);

            if (message_data['chat_id'] in chat_list) {
                chat_list[message_data['chat_id']]['last_message'] = message_data;
                console.log(chat_list);
                setChats(chat_list);
            }

            if (message_data['chat_id'] === chat.id) {
                addMessage(message_data, styles.messageInterlocutor);
                return;
            }
        });
    }, [addMessage, chat, chat_list]);

    async function getChatMessages(chat_id, interlocutor_id) {
        if (chat.id === chat_id)
            return;

        const response = await axios.get(`${config.url}/message/chat/${chat_id}`, auth_headers());

        let result = [];
        
        Object.entries(response.data.items).forEach((data) => {
            let style = data.author.username === user.username ? styles.messageOwn : styles.messageInterlocutor;

            result.push(<Message key={keygen()} message={data} style={style}/>);
        });

        setChat({id: Number(chat_id), interlocutor: Number(interlocutor_id), messages: result});
    }

    function sendMessage() {
        let message = messageContent.current.innerHTML.replaceAll('&nbsp;', '').trim();

        if (message === '')
            return;

        message = message.replaceAll('<br>', '\n');

        let _config = auth_headers();
        _config.params = { message: message };

        connection.send(chat.interlocutor, message);
        addMessage({content: message, created_at: new Date()}, styles.messageOwn);

        // chat_list[chat.id]['last_message'] = message;

        messageContent.current.innerHTML = '';
    }

    function filter_chats(filter_string) {
        if (filter_string === '') {
            setFilter(null);
            return;
        }

        let escapedInput = filter_string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
        let filter = new RegExp(`(\\S*)${escapedInput}(\\S*)`);

        let result = Object.fromEntries(Object.entries(chat_list).filter(([_, data]) => String(data.interlocutor.username).match(filter)));

        if (filtered_list === null || !(JSON.stringify(filtered_list) === JSON.stringify(result)))
            setFilter(result);
    }

    return (currentUserExist()) ? (
        <div className={styles.base}>
            <nav>
                <header>
                    <button onClick={() => { clearCurrentUser() } }><h3>Welcome, {user.username}</h3></button>
                </header>
                <div className={styles.searchBar}>
                    <input onClick={() => {
                        $(`.${styles.searchBar}`).addClass(styles.searchBarSelected);
                    }} onBlur={() => {
                        $(`.${styles.searchBar}`).removeClass(styles.searchBarSelected);
                    }} onChange={(event) => {
                        filter_chats(event.target.value);
                    }} type='search' />
                    <FontAwesomeIcon icon={faMagnifyingGlass} style={
                        { height: "1rem", marginLeft: ".5rem", }
                    }/>
                </div>
                {
                    chat_list === null ? null : (
                        Object.entries(chat_list).length === 0 ? <span>No chats :(</span> : (
                            <div className={styles.chatList}>
                                {
                                    filtered_list !== null && Object.entries(filtered_list).length === 0 ? <span>Not found :(</span> : (
                                        Object.entries(filtered_list === null ? chat_list : filtered_list).map(([id, data]) => (
                                            <div key={keygen()} className={styles.chat} onClick={() => getChatMessages(id, data.interlocutor.id)}>
                                                <Chat data={data}/>
                                            </div>
                                        ))
                                    )
                                }
                            </div>
                        )
                    )
                }
            </nav>
            {  chat.id === undefined ? null : (
                <main>
                    <div className={styles.messageContainer}>
                        { chat.messages }
                    </div>
                    <footer>
                        <div className={styles.messageInput} contentEditable={true} ref={messageContent}/>

                        <FontAwesomeIcon onClick={sendMessage} icon={faPaperPlane} style={{color: "black"}} />
                    </footer>
                </main>
            ) }
        </div>
    ) : (<Navigate to="/auth"/>);
}


export default Main;
