import React from 'react';
import {Navigate} from "react-router-dom";

import {clearCurrentUser, currentUser} from "../hooks/current_user";
import '../css/index.css';

function Main() {
    const user = currentUser();

    return (user !== null) ? (
        <>
            <div className="menu-welcome">
                <h1>Welcome, {user.username}!</h1>
                <button onClick={() => {clearCurrentUser(); window.location.href = '/auth';}}>LogOut</button>
            </div>
            <h2>Please, select the chat</h2>
        </>
    ) : (<Navigate to="/auth"/>);
}

export default Main;
