import axios from "axios";
import {useCookies} from "react-cookie";

async function Login(email, password) {
    const [cookies, setCookie, useCookie] = useCookies(["user"]);

    setCookie("test", "test1", {path: "/"});

    const params = new URLSearchParams({ 'username': email, 'password': password });

    axios.post('http://localhost:443/auth/jwt/login', params).then((response) => {
        setCookie("access_token", response.data.t, {path: "/"});
        console.log(response);
    }).catch((error) => {
        console.log(error);
    });
}

export default Login;
