import React, {useRef, useState} from 'react';
import {Navigate} from "react-router-dom";
import axios from "axios";

import {auth_headers, clearCurrentUser, currentUser, currentUserExist} from "../../controllers/userController";
import styles from '../../css/index.module.css';
import Chat from "./MainSelectChat";
import config from "../../config";
import Message from "./MainMessage";
import connection from "../../controllers/websocketController";


function Main() {
    const user = currentUser();

    const messageContent = useRef(null);

    const [chats, setChats] = useState([]);
    const [messages, setMessages] = useState([]);
    const [currentChatId, setCurrentChatId] = useState(-1);

    let current_key = -1;

    async function GetChatMessages(chat_id, interlocutor_id) {
        const response = await axios.get(`${config.url}/message/chat/${chat_id}`, auth_headers());

        let result = [];

        for (let index = 0; index < response.data.total; index++) {
            let item = response.data.items[index];

            let style = item.author.username === user.username ? styles.messageOwn : styles.messageInterlocutor;

            result.push(<Message key={item.id} data={item} style={style}/>);

            if (index === response.data.total - 1)
                current_key = item.id + 1;
        }

        setMessages(result);
        setCurrentChatId(chat_id);

        connection.connect(interlocutor_id);
    }


    function SendMessage() {
        let message = messageContent.current.innerHTML.replace('<br>', '\n');

        if (message === '')
            return;

        let _config = auth_headers();
        _config.params = { message: message };

        connection.send(message);

        setMessages([<Message key={current_key++} data={message} style={styles.messageOwn}/>, ...messages]);

        messageContent.current.innerHTML = '';
    }

    if (chats.length === 0) {
        axios.get(`${config.url}/chat/all`, auth_headers()).then((response) => {
            let result = [];

            for (let index = 0; index < response.data.total; index++) {
                let item = response.data.items[index];

                result.push(<Chat key={item.id} data={item} onClickFunction={GetChatMessages}/>);
            }

            setChats(result);
        }).catch((error) => {
            if (error.response.request.status === 401) {
                clearCurrentUser();
                window.location.href = '/auth'
            }
        })
    }

    return (currentUserExist()) ? (
        <div className={styles.base}>
            <nav>
                <header>
                    <button onClick={() => { clearCurrentUser() } }><h3>Welcome, {user.username}</h3></button>
                </header>
                <div>
                    { chats }
                </div>
            </nav>
            { currentChatId === -1 ? null : (
                <main>
                    <div className={styles.messageContainer}>
                        { messages }
                    </div>
                    <footer>
                        <div className={styles.messageInput} contentEditable={true} ref={messageContent}/>
                        <button onClick={SendMessage}>Send</button>
                    </footer>
                </main>
            )}
        </div>
    ) : (<Navigate to="/auth"/>);
}

export default Main;
