import styles from "../../css/index.module.css";
import Chat from "./MainSelectChat";
import {useState} from "react";

function LeftPanel({ chats, filter, onClickFunction }) {
    const [new_users, setNewUsers] = useState();

    if (chats === null) return;

    let data = filter === null ? chats : Object.fromEntries(Object.entries(chats).filter(([_, value]) => filter.test(value.interlocutor.username)));

    return (
        <>
            <div className={styles.chatList}>
                { Object.entries(data).map(([key, value]) => (<div key={key} onClick={() => onClickFunction({id: key, ...value})} className={styles.chat}><Chat data={value}/></div>)) }
            </div>
            <div>
                {  }
            </div>
        </>
    )
}

export default LeftPanel;
