import React, {useEffect, useState} from 'react';
import {Navigate} from "react-router-dom";
import axios from "axios";

import {auth_headers, clearCurrentUser, currentUser, currentUserExist} from "../../controllers/userController";
import styles from '../../css/index.module.css';
import SelectChat from "./MainSelectChat";
import config from "../../config";


function Main() {
    const user = currentUser();

    const [chats, setChats] = useState([]);
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        async function getChats() {
            const response = await axios.get(`${config.url}/chat/all`, auth_headers());

            return response.data.items;
        }

        if (!currentUserExist(user) || chats.length !== 0)
            return;
        
        getChats().then(value => {setChats(value)});
    }, [chats.length, user]);

    async function GetChatMessages(chat_id) {
        const response = await axios.get(`${config.url}/message/from_chat/${chat_id}`, auth_headers())

        response.foreach()
    }

    return (currentUserExist(user)) ? (
        <div id={styles.container}>
            <nav>
                <header>
                    <button onClick={() => clearCurrentUser()}><h3>Welcome, {user.username}</h3></button>
                </header>
                <div>
                    {chats.map((data, index) => <SelectChat key={index} data={data}/>)}
                </div>
            </nav>
            <main>

            </main>
        </div>
    ) : (<Navigate to="/auth"/>);
}

export default Main;
