import '../css/form.css';
import axios from "axios";
import {useCookies} from "react-cookie";

async function Login(email, password) {
    const [cookies, setCookie] = useCookies(["user"]);

    const params = new URLSearchParams({ 'username': email, 'password': password });

    axios.post('http://localhost:443/auth/jwt/login', params).then((response) => {
        // setCookie("access_token", response.data.t, {path: "/"});
        console.log(response);
    }).catch((error) => {
        console.log(error);
    });
}

function Authorization() {
    const [cookies, setCookie] = useCookies(["user"]);

    async function Submit(data) {
        data.preventDefault();

        await Login(data.target[0].value, data.target[1].value);
    }

    return (
        <form className='form-registration' onSubmit={Submit}>
            <h3>email</h3>
            <input type='email'/>
            <h3>password</h3>
            <input type='password'/>
            <button>Submit</button>
        </form>
    );
}

export default Authorization;
