import styles from "../../css/index.module.css";
import Chat from "./MainSelectChat";
import {useState} from "react";
import axios from "axios";
import config from "../../config";
import {auth_headers} from "../../controllers/userController";

function LeftPanel({ chats, filter, onClickFunction }) {
    const [new_users, setNewUsers] = useState();

    if (chats === null) return;

    let data;

    if (filter !== null) {
        data = Object.fromEntries(Object.entries(chats).filter(([_, value]) => filter.test(value.interlocutor.username)));

        axios.get(`${config.url}/chat/to_create_with/${filter.toString()}`, auth_headers()).then(
            (response) => setNewUsers(response.data.items));
    } else { data = chats }

    console.log(new_users);

    return (
        <>
            <div className={styles.chatList}>
                { Object.entries(data).map(([key, value]) => (<div key={key} onClick={
                    () => onClickFunction({id: key, ...value})} className={styles.chat}><Chat data={value}/></div>)) }
            </div>
            <div>
                {  }
            </div>
        </>
    )
}

export default LeftPanel;
