import config from "../config";


class WebsocketController {
    constructor() {
        this.current = null;
    }

    connect = (user_id) => {
        this.current = new WebSocket(`${config.ws_url}/send/user/${user_id}`);
    }

    disconnect = () => {
        this.current.close()
        this.current = null;
    }

    send = (message) => {
        if (this.current === null)
            return;

        this.current.send(message);
    }
}

let connection = new WebsocketController();

export default connection;
