import {clearCurrentUser, currentUser} from "../hooks/current_user";
import '../css/index.css';
import {Navigate} from "react-router-dom";
import {useCookies} from "react-cookie";
import Cookies from 'js-cookie'

function Main() {
    const [cookies, setCookie] = useCookies(["test"]);

    setCookie('test', 'flex');

    Cookies.get('test');

    return (currentUser() !== null) ? (
        <div className="test-menu">
            <h3>Hello, {currentUser().username}</h3>
            <button onClick={() => {clearCurrentUser(); window.location.href='/auth';}}>LogOut</button>
            <button onClick={() => console.log(currentUser())}>Test</button>
        </div>
    ) : (<Navigate to="/auth"/>);
}

export default Main;
